defmodule OMG.Eth.Tenderly.ClientTest do
  use ExUnit.Case, async: true
  use ExVCR.Mock, adapter: ExVCR.Adapter.Hackney

  alias ExVCR.Config
  alias OMG.Eth.Tenderly.Client
  alias OMG.Eth.Tenderly.Client.SimulateRequest

  setup do
    Application.ensure_all_started(:ssl)
    Config.cassette_library_dir("test/fixtures/vcr_cassettes/tenderly")
    :ok
  end

  test "gets simulation data from tenderly" do
    use_cassette "simulation_success" do
      request = %SimulateRequest{
        from: "0x6878616891f0e320f0b52906d4cba11677acd772",
        to: "0x584128cad14df97a6eb38c83293a23bd3297d342",
        input:
          "",
        value: 14_000_000_000_000_000,
        block_number: 8_253_234,
        transaction_index: 12,
        gas: 657_970
      }

      {:ok, %{"transaction" => _}} = Client.simulate_transaction(request)
    end
  end
end
